CREATE SCHEMA IF NOT EXISTS cal;

-- Companies
CREATE TABLE IF NOT EXISTS cal.companies (
    id INTEGER PRIMARY KEY,
    name VARCHAR(200) NOT NULL,
    slug VARCHAR(50) UNIQUE NOT NULL,
    subscription_plan VARCHAR(50) DEFAULT 'basic',
    max_users INTEGER DEFAULT 1,
    max_tools INTEGER DEFAULT 50,
    is_active BOOLEAN DEFAULT true,
    created_at TIMESTAMPTZ DEFAULT NOW(),
    updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Users
CREATE TABLE IF NOT EXISTS cal.users (
    id INTEGER PRIMARY KEY,
    email VARCHAR(255) UNIQUE NOT NULL,
    password_hash VARCHAR(255) NOT NULL,
    first_name VARCHAR(100),
    last_name VARCHAR(100),
    role VARCHAR(50) DEFAULT 'user',
    company_id INTEGER REFERENCES cal.companies(id),
    is_active BOOLEAN DEFAULT true,
    last_login_at TIMESTAMPTZ,
    created_at TIMESTAMPTZ DEFAULT NOW(),
    updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Tools (equipment/instruments)
CREATE TABLE IF NOT EXISTS cal.tools (
    id INTEGER PRIMARY KEY,
    company_id INTEGER REFERENCES cal.companies(id),
    number VARCHAR(100),
    serial_number VARCHAR(200),
    manufacturer VARCHAR(200),
    model VARCHAR(200),
    description TEXT,
    type VARCHAR(100),
    frequency VARCHAR(50),
    calibration_status VARCHAR(50),
    tool_status VARCHAR(50) DEFAULT 'active',
    location VARCHAR(200),
    building VARCHAR(100),
    ownership VARCHAR(200),
    last_calibration_date TIMESTAMPTZ,
    next_due_date TIMESTAMPTZ,
    created_at TIMESTAMPTZ DEFAULT NOW()
);

-- Calibration records
CREATE TABLE IF NOT EXISTS cal.calibrations (
    id INTEGER PRIMARY KEY,
    record_number VARCHAR(100),
    tool_id INTEGER REFERENCES cal.tools(id),
    calibration_date TIMESTAMPTZ,
    result VARCHAR(50),
    next_due_date TIMESTAMPTZ,
    technician VARCHAR(200),
    comments TEXT,
    created_at TIMESTAMPTZ DEFAULT NOW()
);

-- Attachments
CREATE TABLE IF NOT EXISTS cal.attachments (
    id INTEGER PRIMARY KEY,
    tool_id INTEGER REFERENCES cal.tools(id),
    calibration_id INTEGER REFERENCES cal.calibrations(id),
    filename VARCHAR(255),
    original_name VARCHAR(255),
    file_size INTEGER,
    mime_type VARCHAR(100),
    uploaded_at TIMESTAMPTZ DEFAULT NOW()
);

-- Settings
CREATE TABLE IF NOT EXISTS cal.settings (
    id INTEGER PRIMARY KEY,
    key VARCHAR(100) NOT NULL,
    value TEXT,
    updated_at TIMESTAMPTZ DEFAULT NOW(),
    company_id INTEGER REFERENCES cal.companies(id)
);

-- Indexes
CREATE INDEX IF NOT EXISTS idx_cal_tools_company ON cal.tools(company_id);
CREATE INDEX IF NOT EXISTS idx_cal_tools_status ON cal.tools(tool_status);
CREATE INDEX IF NOT EXISTS idx_cal_tools_number ON cal.tools(company_id, number);
CREATE INDEX IF NOT EXISTS idx_cal_calibrations_tool ON cal.calibrations(tool_id);
CREATE INDEX IF NOT EXISTS idx_cal_calibrations_date ON cal.calibrations(calibration_date);
CREATE INDEX IF NOT EXISTS idx_cal_calibrations_due ON cal.calibrations(next_due_date);
CREATE INDEX IF NOT EXISTS idx_cal_users_company ON cal.users(company_id);

-- Row Level Security
ALTER TABLE cal.companies ENABLE ROW LEVEL SECURITY;
ALTER TABLE cal.users ENABLE ROW LEVEL SECURITY;
ALTER TABLE cal.tools ENABLE ROW LEVEL SECURITY;
ALTER TABLE cal.calibrations ENABLE ROW LEVEL SECURITY;
ALTER TABLE cal.attachments ENABLE ROW LEVEL SECURITY;
ALTER TABLE cal.settings ENABLE ROW LEVEL SECURITY;

CREATE POLICY "service_role_all" ON cal.companies FOR ALL TO service_role USING (true) WITH CHECK (true);
CREATE POLICY "service_role_all" ON cal.users FOR ALL TO service_role USING (true) WITH CHECK (true);
CREATE POLICY "service_role_all" ON cal.tools FOR ALL TO service_role USING (true) WITH CHECK (true);
CREATE POLICY "service_role_all" ON cal.calibrations FOR ALL TO service_role USING (true) WITH CHECK (true);
CREATE POLICY "service_role_all" ON cal.attachments FOR ALL TO service_role USING (true) WITH CHECK (true);
CREATE POLICY "service_role_all" ON cal.settings FOR ALL TO service_role USING (true) WITH CHECK (true);

-- Grant schema access to postgres role (used by direct connection)
GRANT USAGE ON SCHEMA cal TO postgres;
GRANT ALL ON ALL TABLES IN SCHEMA cal TO postgres;
GRANT ALL ON ALL SEQUENCES IN SCHEMA cal TO postgres;

-- Sequences for future inserts
CREATE SEQUENCE IF NOT EXISTS cal.companies_id_seq START WITH 100;
CREATE SEQUENCE IF NOT EXISTS cal.users_id_seq START WITH 100;
CREATE SEQUENCE IF NOT EXISTS cal.tools_id_seq START WITH 200;
CREATE SEQUENCE IF NOT EXISTS cal.calibrations_id_seq START WITH 200;
CREATE SEQUENCE IF NOT EXISTS cal.attachments_id_seq START WITH 100;
CREATE SEQUENCE IF NOT EXISTS cal.settings_id_seq START WITH 100;

